import { type } from "../actions/type";


const initialState = {
  countNumber: 0,
};

 export   const reducer = (state=0, action)=>{

    switch (action.type) {
        case type.add: {
            return {
                countNumber: state.countNumber + action.payLoad}

        }
        case type.dec :{
            return (state-1)
        }
        default :
            return (state)

    }
}
